import axios from 'axios';
import React, { useEffect } from 'react'
import { connect, useSelector, useDispatch } from "react-redux";

export default function Home(props) {
    //useSelector: dùng để lấy state từ redux về
    const { mangPhim } = useSelector(state => state.PhimReducer);
    const dispatch = useDispatch();

    useEffect(async () => {
        //hàm chạy sau khi dữ liệu render
        try {
            const result = await axios({
                url: 'https://movie0706.cybersoft.edu.vn/api/QuanLyPhim/LayDanhSachPhim?maNhom=GP01',
                method: 'GET'
            });
            //dispatch dữ liệu lên redux
            dispatch({
                type: 'SET_MANG_PHIM',
                mangPhim: result.data
            });
        } catch (errors) {
            console.log(errors);
        }
    }, [])
    renderListPhim = () => {
        return this.state.mangPhim.map((phim, index) => {
            return <div className='col-4 mt-5' key={index}>
                <div className="card text-white bg-primary" >
                    <img className="card-img-top" src={phim.hinhAnh} alt />
                    <div className="card-body">
                        <h4 className="card-title">{phim.tenPhim}</h4>
                        <p className="card-text">{phim.moTa}</p>
                    </div>
                </div>
            </div>
        })
    }
    return (
        <div className="container">
            <h3 className="text-center">Danh Sách Phim</h3>
            <div className="row">
                {this.renderListPhim()}
            </div>
        </div>
    )
}
