const stateDefault = {
    isLoading: false
}

export const LoadingReducer = (state = stateDefault, action) => {

    switch (action.type) {

        case 'DISPLAY_LOADING': {
            return { ...state, isLoadingL: true }
        }

        case 'HIDE_LOADING': {
            return { ...state, isLoading: false }
        }
        default: return { ...state }

    }
}
